-- ----------------------------
-- Table structure for hotel
-- ----------------------------
DROP TABLE IF EXISTS `hotel`;
CREATE TABLE `hotel`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `abridge` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '缩略词，方便显示，最多5个字',
  `englishName` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `header` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `price` int(11) NULL DEFAULT NULL COMMENT '价格',
  `threshold` int(11) NULL DEFAULT NULL COMMENT '最低消费',
  `buildTime` datetime NULL DEFAULT NULL COMMENT '开业时间',
  `sales` int(11) NULL DEFAULT NULL COMMENT '订单数',
  `promise` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '服务承诺',
  `introduce` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '介绍',
  `inTime` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '14:00-00:00' COMMENT '入住时间',
  `outTime` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '08:00-12:00' COMMENT '离店时间',
  `childInfo` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '酒店允许携带儿童入住;不接受16岁以下客人在无监护人陪同的情况下入住' COMMENT '儿童政策',
  `breakfastInfo` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '酒店不提供早餐' COMMENT '早餐政策',
  `petInfo` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '不可携带宠物' COMMENT '宠物政策',
  `number` int(11) NULL DEFAULT NULL COMMENT '房间数',
  `remain` int(11) NULL DEFAULT NULL COMMENT '剩余房间数',
  `state` int(11) NULL DEFAULT 1 COMMENT '房间状态，1：可订；0：已满',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 13 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

